import { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import { Button } from '@/components/ui/button';
import { ZoomIn, ZoomOut, Locate, Layers } from 'lucide-react';
import type { TerritoryWithUser } from '@shared/schema';
import { DEFAULT_CENTER, getCurrentPosition } from '@/lib/geolocation';

interface MapViewProps {
  territories: TerritoryWithUser[];
  center?: { lat: number; lng: number };
  onLocationFound?: (coords: { lat: number; lng: number }) => void;
}

export function MapView({ territories, center = DEFAULT_CENTER, onLocationFound }: MapViewProps) {
  const mapContainer = useRef<HTMLDivElement>(null);
  const mapRef = useRef<L.Map | null>(null);
  const tileLayerRef = useRef<L.TileLayer | null>(null);
  const [isLocating, setIsLocating] = useState(false);
  const [mapStyle, setMapStyle] = useState<'light' | 'dark'>('light');

  useEffect(() => {
    if (!mapContainer.current || mapRef.current) return;

    // Initialize map with more zoom
    const map = L.map(mapContainer.current, {
      center: [center.lat, center.lng],
      zoom: 14,
      zoomControl: false,
      attributionControl: false,
    });

    // Use Cartodb Positron for clean, minimalist look
    const lightTiles = L.tileLayer('https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png', {
      maxZoom: 19,
    });

    lightTiles.addTo(map);
    tileLayerRef.current = lightTiles;
    mapRef.current = map;

    return () => {
      map.remove();
      mapRef.current = null;
    };
  }, []);

  const toggleMapStyle = () => {
    if (!mapRef.current || !tileLayerRef.current) return;

    const newStyle = mapStyle === 'light' ? 'dark' : 'light';

    // Remove current tile layer
    mapRef.current.removeLayer(tileLayerRef.current);

    // Add new tile layer
    const newTiles = newStyle === 'light'
      ? L.tileLayer('https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png', { maxZoom: 19 })
      : L.tileLayer('https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png', { maxZoom: 19 });

    newTiles.addTo(mapRef.current);
    tileLayerRef.current = newTiles;
    setMapStyle(newStyle);
  };

  useEffect(() => {
    if (!mapRef.current) return;

    // Clear existing territory layers
    mapRef.current.eachLayer((layer) => {
      if (layer instanceof L.Polygon) {
        mapRef.current?.removeLayer(layer);
      }
    });

    // Add territory polygons with improved styling
    territories.forEach((territory) => {
      if (!mapRef.current || !territory.geometry?.coordinates) return;

      const coordinates = territory.geometry.coordinates[0].map((coord: number[]) => 
        [coord[1], coord[0]] as [number, number]
      );

      const polygon = L.polygon(coordinates, {
        color: territory.user.color,
        fillColor: territory.user.color,
        fillOpacity: 0.4,
        weight: 3,
        opacity: 0.8,
        className: 'territory-polygon',
      });

      // Improved popup styling
      polygon.bindPopup(`
        <div class="p-3 min-w-[200px]">
          <div class="flex items-center gap-2 mb-2">
            <div class="w-4 h-4 rounded-full" style="background-color: ${territory.user.color}"></div>
            <strong class="text-base">${territory.user.name}</strong>
          </div>
          <div class="text-sm text-muted-foreground">
            <div class="flex items-center justify-between">
              <span>Área conquistada:</span>
              <span class="font-semibold text-foreground">${territory.area.toLocaleString('es-ES', { maximumFractionDigits: 0 })} m²</span>
            </div>
          </div>
        </div>
      `, {
        className: 'custom-popup',
      });

      // Add hover effect
      polygon.on('mouseover', function() {
        this.setStyle({
          fillOpacity: 0.6,
          weight: 4,
        });
      });

      polygon.on('mouseout', function() {
        this.setStyle({
          fillOpacity: 0.4,
          weight: 3,
        });
      });

      polygon.addTo(mapRef.current);
    });
  }, [territories]);

  const handleZoomIn = () => {
    mapRef.current?.zoomIn();
  };

  const handleZoomOut = () => {
    mapRef.current?.zoomOut();
  };

  const handleLocate = async () => {
    setIsLocating(true);
    try {
      const position = await getCurrentPosition();
      mapRef.current?.setView([position.lat, position.lng], 16);

      // Add pulsing marker for current location
      const pulsingIcon = L.divIcon({
        className: 'current-location-marker',
        html: `
          <div class="relative">
            <div class="absolute w-8 h-8 bg-primary/30 rounded-full animate-ping"></div>
            <div class="relative w-4 h-4 bg-primary rounded-full border-2 border-white shadow-lg"></div>
          </div>
        `,
        iconSize: [32, 32],
        iconAnchor: [16, 16],
      });

      L.marker([position.lat, position.lng], {
        icon: pulsingIcon,
      }).addTo(mapRef.current!);

      onLocationFound?.(position);
    } catch (error) {
      console.error('Error getting location:', error);
    } finally {
      setIsLocating(false);
    }
  };

  return (
    <div className="relative w-full h-full">
      <div ref={mapContainer} className="w-full h-full map-container" data-testid="map-container" />

      {/* Zoom Controls */}
      <div className="absolute right-4 top-1/2 -translate-y-1/2 flex flex-col gap-2 z-[1000]">
        <Button
          size="icon"
          variant="secondary"
          onClick={handleZoomIn}
          data-testid="button-zoom-in"
          className="shadow-lg bg-card/95 backdrop-blur-sm hover:bg-card border-border"
        >
          <ZoomIn className="h-5 w-5" />
        </Button>
        <Button
          size="icon"
          variant="secondary"
          onClick={handleZoomOut}
          data-testid="button-zoom-out"
          className="shadow-lg bg-card/95 backdrop-blur-sm hover:bg-card border-border"
        >
          <ZoomOut className="h-5 w-5" />
        </Button>
        <Button
          size="icon"
          variant="secondary"
          onClick={toggleMapStyle}
          data-testid="button-toggle-style"
          className="shadow-lg bg-card/95 backdrop-blur-sm hover:bg-card border-border"
        >
          <Layers className="h-5 w-5" />
        </Button>
      </div>

      {/* Locate Button */}
      <Button
        size="icon"
        variant="secondary"
        onClick={handleLocate}
        disabled={isLocating}
        data-testid="button-locate"
        className="absolute left-4 bottom-24 shadow-lg z-[1000] bg-card/95 backdrop-blur-sm hover:bg-card border-border"
      >
        <Locate className={`h-5 w-5 ${isLocating ? 'animate-pulse' : ''}`} />
      </Button>

      <style>{`
        .map-container {
          border-radius: 0;
        }

        .territory-polygon {
          transition: all 0.2s ease;
        }

        .leaflet-popup-content-wrapper {
          border-radius: 0.5rem;
          padding: 0;
          overflow: hidden;
        }

        .leaflet-popup-content {
          margin: 0;
        }

        .leaflet-popup-tip {
          background: hsl(var(--card));
        }

        @keyframes ping {
          75%, 100% {
            transform: scale(2);
            opacity: 0;
          }
        }

        .animate-ping {
          animation: ping 1.5s cubic-bezier(0, 0, 0.2, 1) infinite;
        }
      `}</style>
    </div>
  );
}

client/src/pages/ProfilePage.tsx.content:
import { useState, useEffect } from 'react';
import { Card } from '@/components/ui/card';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { ScrollArea } from '@/components/ui/scroll-area';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import { User, Trophy, MapPin, Users, Settings, LogOut, Link2, Unlink, Loader2, RefreshCw } from 'lucide-react';
import { SiStrava } from 'react-icons/si';
import { LoadingState } from '@/components/LoadingState';
import { SettingsDialog } from '@/components/SettingsDialog';
import { LoginDialog } from '@/components/LoginDialog';
import { useSession } from '@/hooks/use-session';
import { useToast } from '@/hooks/use-toast';
import { useQuery, useMutation } from '@tanstack/react-query';
import { apiRequest, queryClient } from '@/lib/queryClient';

interface StravaStatus {
  connected: boolean;
  athleteData?: {
    firstname?: string;
    lastname?: string;
    profile_medium?: string;
    city?: string;
    country?: string;
  };
  lastSyncAt?: string | null;
}

export default function ProfilePage() {
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [isLogoutDialogOpen, setIsLogoutDialogOpen] = useState(false);
  const [isLoginOpen, setIsLoginOpen] = useState(false);
  const [isStravaDisconnectOpen, setIsStravaDisconnectOpen] = useState(false);
  const { toast } = useToast();
  const { user, isLoading, logout, login } = useSession();

  // Strava status query - use explicit queryFn to build correct URL
  const stravaStatusKey = `/api/strava/status/${user?.id}`;
  const { data: stravaStatus, isLoading: isStravaLoading } = useQuery<StravaStatus>({
    queryKey: [stravaStatusKey],
    enabled: !!user?.id,
  });

  // Strava connect mutation
  const connectStravaMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('GET', `/api/strava/connect?userId=${user?.id}`);
      const data = await response.json();
      return data;
    },
    onSuccess: (data) => {
      if (data.authUrl) {
        window.location.href = data.authUrl;
      }
    },
    onError: () => {
      toast({
        title: 'Error',
        description: 'No se pudo conectar con Strava',
        variant: 'destructive',
      });
    },
  });

  // Strava disconnect mutation
  const disconnectStravaMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', '/api/strava/disconnect', { userId: user?.id });
      const data = await response.json();
      return data;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [stravaStatusKey] });
      toast({
        title: 'Strava desconectado',
        description: 'Tu cuenta de Strava ha sido desvinculada',
      });
      setIsStravaDisconnectOpen(false);
    },
    onError: () => {
      toast({
        title: 'Error',
        description: 'No se pudo desconectar Strava',
        variant: 'destructive',
      });
    },
  });

  // Process pending Strava activities
  const processMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', `/api/strava/process/${user?.id}`);
      const data = await response.json();
      return data;
    },
    onSuccess: (data) => {
      if (data.processed > 0) {
        queryClient.invalidateQueries({ queryKey: ['/api/territories'] });
        queryClient.invalidateQueries({ queryKey: ['/api/routes', user?.id] });
        queryClient.invalidateQueries({ queryKey: ['/api/user', user?.id] });
        toast({
          title: 'Actividades procesadas',
          description: `Se procesaron ${data.processed} actividades de Strava`,
        });
      } else {
        toast({
          title: 'Sin actividades nuevas',
          description: 'No hay actividades pendientes de procesar',
        });
      }
    },
    onError: () => {
      toast({
        title: 'Error',
        description: 'No se pudieron procesar las actividades',
        variant: 'destructive',
      });
    },
  });

  // Check for Strava OAuth callback results
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    if (params.get('strava_connected') === 'true') {
      toast({
        title: 'Strava conectado',
        description: 'Tu cuenta de Strava ha sido vinculada exitosamente',
      });
      if (user?.id) {
        queryClient.invalidateQueries({ queryKey: [`/api/strava/status/${user.id}`] });
      }
      window.history.replaceState({}, '', window.location.pathname);
    } else if (params.get('strava_error')) {
      const error = params.get('strava_error');
      let message = 'Hubo un problema conectando con Strava';
      if (error === 'denied') message = 'Acceso denegado por el usuario';
      if (error === 'already_linked') message = 'Esta cuenta de Strava ya esta vinculada a otro usuario';
      toast({
        title: 'Error de Strava',
        description: message,
        variant: 'destructive',
      });
      window.history.replaceState({}, '', window.location.pathname);
    }
  }, [toast, user?.id]);

  const handleLogout = () => {
    logout();
    toast({
      title: 'Sesion cerrada',
      description: 'Has cerrado sesion exitosamente',
    });
    setIsLogoutDialogOpen(false);
    setIsLoginOpen(true);
  };

  if (isLoading) {
    return <LoadingState message="Cargando perfil..." />;
  }

  if (!user) {
    return (
      <div className="flex flex-col items-center justify-center h-full p-6">
        <div className="text-center space-y-4">
          <div className="relative inline-block">
            <User className="h-20 w-20 mx-auto text-muted-foreground" />
          </div>
          <h2 className="text-2xl font-bold">No has iniciado sesion</h2>
          <p className="text-muted-foreground">
            Inicia sesion para acceder a tu perfil
          </p>
          <Button
            onClick={() => setIsLoginOpen(true)}
            data-testid="button-login-prompt"
          >
            Iniciar sesion
          </Button>
        </div>
        <LoginDialog open={isLoginOpen} onOpenChange={setIsLoginOpen} onLogin={login} />
      </div>
    );
  }

  const getInitials = (name: string) => {
    return name
      .split(' ')
      .map((n) => n[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  return (
    <ScrollArea className="h-full">
      <div className="flex flex-col min-h-full">
        <div className="p-6 border-b border-border bg-gradient-to-br from-primary/5 to-transparent">
          <h1 className="text-3xl font-bold flex items-center gap-3">
